func:
// ----------------------------------------------------------------------------
// DWD-Warnungen - NodeRED-Funktion
// Version: 1.15
// Datum: 15.04.2023
// Quelle: https://github.com/migacode/home-assistant
// ----------------------------------------------------------------------------
// Initialisierung
// ----------------------------------------------------------------------------
// Entitäten der DWD-Sensoren (vollständige Namen)
var dwd_entity_name_cw = 'sensor.dwd_warnungen_hoerstel_current_warning_level';
var dwd_entity_name_aw = 'sensor.dwd_warnungen_hoerstel_advance_warning_level';
// ----------------------------------------------------------------------------
// Alten Payload löschen um keine Daten daraus mit weiterzuleiten
msg.payload = [];
// Symbole und Textbausteine definieren
var weather_warnidx = ['EIS', 'FROST', 'GEWITTER', 'HAGEL', 'HITZE', 'KALT', 'KÄLTE', 'ORKAN', 'REGEN', 'SCHNEE', 'STURM', 'WASSER', 'WIND'];
var weather_symbols = ['❄️', '❄️', '🌩', '🌩', '☀️', '❄️', '❄️', '🌪', '☂️', '⛄️', '🌪', '🌊', '💨'];
var blitz = '⚡️';
var trennlinie = '\n───\n';
// Status aller Home Assistant Entitäten einlesen
var global_states = global.get('homeassistant.homeAssistant.states');
// Daten der beiden DWD-Sensoren extrahieren
var entity_current_data = global_states[dwd_entity_name_cw];
var entity_advance_data = global_states[dwd_entity_name_aw];
// Anzahl der vorhandenen Warnmeldungen ermitteln
var number_of_current_warnings = parseInt(entity_current_data.attributes.warning_count);
var number_of_advance_warnings = parseInt(entity_advance_data.attributes.warning_count);
var number_of_all_warnings = number_of_current_warnings + number_of_advance_warnings;
// Nur weitermachen, wenn es Warnmeldungen gibt
if (number_of_all_warnings > 0)
{
  var full_message = blitz + " ***DWD-WARNUNG" + ((number_of_all_warnings > 1) ? 'EN (' + number_of_all_warnings + ')': '') + "*** " + blitz + " (über Node-RED)" + trennlinie;
  var short_message = full_message;
  // ----------------------------------------------------------------------------
  // "Aktuelle" Warnmeldungen verarbeiten
  // ----------------------------------------------------------------------------
  if (number_of_current_warnings > 0)
  {
    for (var i = 1; i <= number_of_current_warnings; i++)
    {
      var current_headline = entity_current_data.attributes['warning_' + i + '_headline'];
      // Wetter-Symbole zur Kopfzeile ermitteln
      var c_weather_symbol = '';
      for (var cw = 0; cw < weather_warnidx.length; cw++) {
        if (current_headline.toUpperCase().indexOf(weather_warnidx[cw]) !== -1) { c_weather_symbol += weather_symbols[cw]; }
      }
      // Zeiten auslesen
      var c_time_start = time_convert(entity_current_data.attributes['warning_' + i + '_start']);
      var c_time_end = time_convert(entity_current_data.attributes['warning_' + i + '_end']);
      // Nachrichten aus den einzelnen Warnmeldungs-Komponenten zusammenbauen und hinzufügen
      short_message += current_headline + " " + c_weather_symbol + "\n" + c_time_start + " bis " + c_time_end + trennlinie;
      full_message += "***" + current_headline + "*** " + c_weather_symbol + "\n" +
      "Aktuelle Warnstufe: ***" + entity_current_data.attributes['warning_' + i + '_level'] + "***\n" +
      entity_current_data.attributes['warning_' + i + '_description'] + "\n" +
        "Von " + c_time_start + " Uhr bis " + c_time_end + " Uhr." + trennlinie;
    }
  }
  // ----------------------------------------------------------------------------
  // "Erweiterte" Warnmeldungen verarbeiten
  // ----------------------------------------------------------------------------
  if (number_of_advance_warnings > 0)
  {
    for (var i = 1; i <= number_of_advance_warnings; i++)
    {
      var advance_headline = entity_advance_data.attributes['warning_' + i + '_headline'];
      // Wetter-Symbole zur Kopfzeile ermitteln
      var a_weather_symbol = '';
      for (var aw = 0; aw < weather_warnidx.length; aw++) {
        if (advance_headline.toUpperCase().indexOf(weather_warnidx[aw]) !== -1) { a_weather_symbol += weather_symbols[aw]; }
      }
      // Zeiten auslesen
      var a_time_start = time_convert(entity_advance_data.attributes['warning_' + i + '_start']);
      var a_time_end = time_convert(entity_advance_data.attributes['warning_' + i + '_end']);
      // Nachrichten aus den einzelnen Warnmeldungs-Komponenten zusammenbauen und hinzufügen
      short_message += advance_headline + " " + a_weather_symbol + "\n" + a_time_start + " bis " + c_time_end + trennlinie;
      full_message += "***" + advance_headline + "*** " + a_weather_symbol + "\n" +
      "Erweiterte Warnstufe: ***" + entity_advance_data.attributes['warning_' + i + '_level'] + "***\n" +
      entity_advance_data.attributes['warning_' + i + '_description'] + "\n" +
      "Von " + a_time_start + " Uhr bis " + a_time_end + " Uhr." + trennlinie;
    }
  }
  // Fertige Nachrichten als Payload an folgende/n Node/s senden
  var newMsg = {};
  newMsg.payload = {
    message: full_message,
    appmsg: short_message.substring(0, 320) + "\n(Details siehe HA-App)"
  }
  node.send(newMsg);
}
// ----------------------------------------------------------------------------
// Funktions-Node OHNE Rückmeldung beenden, da bereits Daten gesendet wurden
// ----------------------------------------------------------------------------
return null;
// ----------------------------------------------------------------------------
// Funktion zur Umwandlung einer ISO-Zeitangabe in ein eigenes Format
// ----------------------------------------------------------------------------
function time_convert(iso_string) {
  var date = new Date(iso_string);
  var year = parseInt(date.getFullYear());
  var month = parseInt(date.getMonth()) + 1;
  var day = parseInt(date.getDate());
  var hour = parseInt(date.getHours());
  var minute = parseInt(date.getMinutes());
  if (day < 10) { day = '0' + day; }
  if (month < 10) { month = '0' + month; }
  if (hour < 10) { hour = '0' + hour; }
  if (minute < 10) { minute = '0' + minute; }
  return '***' + day + '.' + month + '.' + year + '*** um ***' + hour + ':' + minute + '***';
}
