// ----------------------------------------------------------------------------
// NINA-Warnungen - NodeRED-Funktion
// Version: 1.15
// Datum: 15.04.2023
// Quelle: https://github.com/migacode/home-assistant
// ----------------------------------------------------------------------------
// Initialisierung
// ----------------------------------------------------------------------------
// EntitÃ¤ten der NINA-Sensoren (Name OHNE Nummer aber mit Unterstrich am Ende)
var nina_entity_name = 'binary_sensor.nina_warning_hoerstel_';
// ----------------------------------------------------------------------------
// Alten Payload lÃ¶schen um keine Daten daraus mit weiterzuleiten
msg.payload = [];
// Symbole und Textbausteine definieren
var weather_warnidx = ['EIS', 'FROST', 'GEWITTER', 'HAGEL', 'HITZE', 'KALT', 'KÃ„LTE', 'ORKAN', 'REGEN', 'SCHNEE', 'STURM', 'WASSER', 'WIND'];
var weather_symbols = ['â„ï¸', 'â„ï¸', 'ğŸŒ©', 'ğŸŒ©', 'â˜€ï¸', 'â„ï¸', 'â„ï¸', 'ğŸŒª', 'â˜‚ï¸', 'â›„ï¸', 'ğŸŒª', 'ğŸŒŠ', 'ğŸ’¨'];
var blitz = 'âš¡ï¸';
var trennlinie = '\nâ”€â”€â”€\n';
// Status aller Home Assistant EntitÃ¤ten einlesen
var global_states = global.get('homeassistant.homeAssistant.states');
// Anzahl beobachteter NINA-Warnungen
var number_of_nina_warnings = 5;
// Anzahl der vorhandenen Warnmeldungen ermitteln
var number_of_active_warnings = 0;
for (var i = 1; i <= number_of_nina_warnings; i++)
{
  var entity_current_data = global_states[nina_entity_name + i];
  if (entity_current_data.state == 'on') { number_of_active_warnings++; }
}
// Nur weitermachen, wenn es Warnmeldungen gibt
if (number_of_active_warnings > 0)
{
  var full_message = blitz + " ***NINA-WARNUNG" + ((number_of_active_warnings > 1) ? 'EN (' + number_of_active_warnings + ')' : '') + "*** " + blitz + " (Ã¼ber Node-RED)" + trennlinie;
  var short_message = full_message;
  for (var i = 1; i <= number_of_nina_warnings; i++)
  {
    // Daten des NINA-Sensors extrahieren
    var entity_current_data = global_states[nina_entity_name + i];
    // Wenn die Warnung aktiv ist, dann Nachricht erstellen
    if (entity_current_data.state == 'on')
    {
      var headline = entity_current_data.attributes.headline;
      // Warnstufen Ã¼bersetzen
      var level = entity_current_data.attributes.severity;
      level = level.replace('Minor', 'Gering').replace('Moderate', 'MÃ¤ÃŸig').replace('Severe', 'Schwer').replace('Extreme', 'Extrem');
      // Wetter-Symbole zur Kopfzeile ermitteln
      var weather_symbol = '';
      for (var w = 0; w < weather_warnidx.length; w++) {
        if (headline.toUpperCase().indexOf(weather_warnidx[w]) !== -1) { weather_symbol += weather_symbols[w]; }
      }
      // Zeiten auslesen
      var time_start = time_convert(entity_current_data.attributes.start);
      var time_end = time_convert(entity_current_data.attributes.expires);
      // Nachrichten aus den einzelnen Warnmeldungs-Komponenten zusammenbauen und hinzufÃ¼gen
      short_message += headline + " " + weather_symbol + "\n" + time_start + " bis " + time_end + trennlinie;
      full_message += "***" + headline + "*** " + weather_symbol + "\n" +
      "Warnstufe: ***" + level + "***\n" +
      entity_current_data.attributes.description + "\n" +
      "Von " + time_start + " Uhr bis " + time_end + " Uhr." + trennlinie;
    }
  }
  // Fertige Nachrichten als Payload an folgende/n Node/s senden
  var newMsg = {};
  newMsg.payload = {
    message: full_message,
    appmsg: short_message.substring(0, 320) + "\n(Details siehe HA-App)"
  }
  node.send(newMsg);
}
// ----------------------------------------------------------------------------
// Funktions-Node OHNE RÃ¼ckmeldung beenden, da bereits Daten gesendet wurden
// ----------------------------------------------------------------------------
return null;
// ----------------------------------------------------------------------------
// Funktion zur Umwandlung einer ISO-Zeitangabe in ein eigenes Format
// ----------------------------------------------------------------------------
function time_convert(iso_string) {
  var date = new Date(iso_string);
  var year = parseInt(date.getFullYear());
  var month = parseInt(date.getMonth()) + 1;
  var day = parseInt(date.getDate());
  var hour = parseInt(date.getHours());
  var minute = parseInt(date.getMinutes());
  if (day < 10) { day = '0' + day; }
  if (month < 10) { month = '0' + month; }
  if (hour < 10) { hour = '0' + hour; }
  if (minute < 10) { minute = '0' + minute; }
  return '***' + day + '.' + month + '.' + year + '*** um ***' + hour + ':' + minute + '***';
}
