func:
// ============================================================================
// Noticade - Looper
// Version: 1.00
// Datum:   01.03.2025
// -----------------------------------------------------------------------------
// Ermittelt die für den laufenden Empfänger konfigurierten Einstellungen,
// löst die entsprechenden Aktionen aus und passt die laufenden Zähler an.
// =============================================================================
// ACHTUNG: In diesem Script keine Änderungen durchführen, wenn Du nicht genau
// weißt was Du tust - die Konfiguration von Noticade erfolgt ausschließlich
// in dem Node "Initialisierung". ;)
// =============================================================================
// -----------------------------------------------------------------------------
// In dem Node Initalisierung gesetzte Variablen einlesen
// -----------------------------------------------------------------------------
var noticade_trigger_id = global.get("noticade_trigger_id");
var noticade_message    = global.get("noticade_message");
var noticade_repeat     = parseInt(global.get("noticade_repeat"));
var noticade_delay      = parseInt(global.get("noticade_delay"));
var noticade_index      = parseInt(global.get("noticade_index"));
var noticade_max_idx    = parseInt(global.get("noticade_max_idx"));
var noticade_loop       = parseInt(global.get("noticade_loop"));

// var this_index = noticade_index; // Nur zum Testen
// var this_loop = noticade_loop;   // Nur zum Testen

// -----------------------------------------------------------------------------
// Aktuelle Entitäten für Ausgabe-Pfade einlesen (VOR der Erhöhung der Zähler)
// -----------------------------------------------------------------------------
var current_ha_app_id = global.get("noticade_app_id_" + noticade_index);
var current_phone_id  = global.get("noticade_phone_id_" + noticade_index);

// -----------------------------------------------------------------------------
// Durchlauf-Zähler erhöhen und bei Maximalwert zum nächsten Empfänger springen
// -----------------------------------------------------------------------------
noticade_loop += 1;
if (noticade_loop > noticade_repeat)
{
  noticade_loop = 1;
  // ---------------------------------------------------------------------------
  // Index des Empfängers erhöhen bzw. zurücksetzen
  // ---------------------------------------------------------------------------
  noticade_index += 1;
  if (noticade_index > noticade_max_idx)
  {
    noticade_index = 0;
  }
  global.set("noticade_index", noticade_index);
}
global.set("noticade_loop", noticade_loop);

// -----------------------------------------------------------------------------
// Neuen Payload zusammenbauen
// -----------------------------------------------------------------------------
msg.payload = [];
var newMsg = {};
newMsg.payload = {
  // noticade_index: this_index, // Nur zum Testen
  // noticade_loop: this_loop,   // Nur zum Testen
  noticade_trigger_id: noticade_trigger_id,
  noticade_message: noticade_message,
  noticade_delay: noticade_delay,
  noticade_ha_app_id: current_ha_app_id,
  noticade_phone_id: current_phone_id
}

// -----------------------------------------------------------------------------
// Ausgaben anstoßen
// (Ausgang 3 dient nur zur Weiterleitung / Fortführung des Flows)
// -----------------------------------------------------------------------------
if (current_ha_app_id != 'undefined' && current_phone_id != 'undefined' &&
    current_ha_app_id != null && current_phone_id != null)
{
  // Ausgabe an HA-Apps und Telefon-Anruf (Ausgang 1, 2 und 3)
  if (current_ha_app_id.toLowerCase() != 'none' && current_phone_id.toLowerCase() != 'none')
  {
    node.send([newMsg, newMsg, newMsg]); // asynchron
    node.done();
    // return [newMsg, newMsg, newMsg]; // synchron
  }
  else
  {
    // Ausgabe an HA-Apps und kein Telefon-Anruf (nur Ausgang 1 und 3)
    if (current_ha_app_id.toLowerCase() != 'none' && current_phone_id.toLowerCase() == 'none')
    {
      node.send([newMsg, null, newMsg]); // asynchron
      node.done();
      // return [newMsg, null, newMsg]; // synchron
    } 
    // Keine Ausgabe an HA-Apps, nur Telefon-Anruf (nur Ausgang 2 und 3)
    if (current_ha_app_id.toLowerCase() == 'none' && current_phone_id.toLowerCase() != 'none')
    {
      node.send([null, newMsg, newMsg]); // asynchron
      node.done();
      // return [null, newMsg, newMsg]; // synchron
    }
  }
}
else
{
  // ---------------------------------------------------------------------------
  // Wenn keine gültigen Informationen vorliegen, keine Ausgabe und kein Anruf
  // ---------------------------------------------------------------------------
  node.send([null, null, newMsg]); // asynchron
  node.done();
  // return [null, null, newMsg]; // synchron
}
return null;
